def run_02a():
    doubles = 0
    triples = 0
    for id in ids:
        print(id)
        letters_count = dict()
        for letter in id:
            if letter in letters_count.keys():
                letters_count[letter] += 1
            else:
                letters_count[letter] = 1
        has_double = 0
        has_triple = 0
        for key, value in letters_count.items():
            if value == 2:
                print(f"Double {key}")
                has_double = 1
            if value == 3:
                print(f"Triple {key}")
                has_triple = 1
        doubles += has_double
        triples += has_triple

    print(f"{doubles} doubles and {triples} triples => {doubles * triples}")

def run_02b():
    couples = []
    for index, id in enumerate(ids):
        for other_index, other_id in enumerate(ids):
            if other_index == index:
                continue
            common_letters = []
            differences = 0
            for i in range(len(id)):
                if id[i] == other_id[i]:
                    common_letters.append(id[i])
                else:
                    differences += 1
            str_common_letters = "".join(common_letters)
            couples.append((differences, str_common_letters))

    minimal_difference = 100
    for difference, common_letters in couples:
        if difference < minimal_difference:
            minimal_difference = difference
            print(f"New minimal difference ({difference}). Best common letters : {common_letters}")
